const saveOptions=()=>{const e=document.getElementById("logs").checked,t=document.getElementById("autoSwitch").checked,n=document.getElementById("albumArt").checked;isTranslateEnabled=document.getElementById("translate").checked,translationLanguage=document.getElementById("translationLanguage").value,chrome.storage.sync.set({isLogsEnabled:e,isAutoSwitchEnabled:t,isAlbumArtEnabled:n,isTranslateEnabled:isTranslateEnabled,translationLanguage:translationLanguage},(()=>{const e=document.getElementById("status");e.textContent="Options saved.",e.classList.add("active"),setTimeout((()=>{e.classList.remove("active"),setTimeout((()=>{e.textContent=""}),200)}),2e3)}))},restoreOptions=()=>{chrome.storage.sync.get({isLogsEnabled:!0,isAutoSwitchEnabled:0,isAlbumArtEnabled:!0,isTranslateEnabled:0,translationLanguage:"en"},(e=>{document.getElementById("logs").checked=e.isLogsEnabled,document.getElementById("albumArt").checked=e.isAlbumArtEnabled,document.getElementById("autoSwitch").checked=e.isAutoSwitchEnabled,document.getElementById("translate").checked=e.isTranslateEnabled,document.getElementById("translationLanguage").value=e.translationLanguage}))};document.addEventListener("DOMContentLoaded",restoreOptions),document.getElementById("save").addEventListener("click",saveOptions);