const saveOptions=()=>{const e=document.getElementById("logs").checked,t=document.getElementById("autoSwitch").checked,n=document.getElementById("albumArt").checked;chrome.storage.sync.set({isLogsEnabled:e,isAutoSwitchEnabled:t,isAlbumArtEnabled:n},(()=>{const e=document.getElementById("status");e.textContent="Options saved.",e.classList.add("active"),setTimeout((()=>{e.classList.remove("active"),setTimeout((()=>{e.textContent=""}),200)}),2e3)}))},restoreOptions=()=>{chrome.storage.sync.get({isLogsEnabled:!0,isAutoSwitchEnabled:0,isAlbumArtEnabled:!0},(e=>{document.getElementById("logs").checked=e.isLogsEnabled,document.getElementById("albumArt").checked=e.isAlbumArtEnabled,document.getElementById("autoSwitch").checked=e.isAutoSwitchEnabled}))};document.addEventListener("DOMContentLoaded",restoreOptions),document.getElementById("save").addEventListener("click",saveOptions);